
public class Math {
	public static int mul(int nUM1, int nUM2) {
		// TODO Auto-generated method stub
		return nUM1*nUM2;
	}

	public static int div(int nUM1, int nUM2) {
		// TODO Auto-generated method stub
		return nUM1/nUM2;
	}

	public static int sub(int nUM1, int nUM2) {
		return nUM1-nUM2;
	}

	public static int add(int nUM1, int nUM2) {
		return nUM1+nUM2;
	}

}
